script:
$.getScript('https://cdn.jsdelivr.net/npm/vue@2.6.11',function(){startVue()});

function startVue() {

	var ItemsVue = new Vue({
		el: '#Itemlist',
		data: {
			items: []
		},
		mounted: function () {
			var self = this;
			$.ajax({
				url: aspLiteAjaxHandler + '?asplEvent=sampleform28_data',
				method: 'GET',
				success: function (data) {
					self.items = data.aspl;
				},
				error: function (error) {
					console.log(error);
				}
			});
		}
	});
}
